import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Domain from './components/Domain';
import Servers from './components/Servers';
import Home from './components/Home';
import Auth from './components/Auth';
import axios from 'axios';

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    const checkAuth = async () => {
      const token = localStorage.getItem('token');
      if (token) {
        try {
          await axios.get('/api/auth/me', {
            headers: { Authorization: `Bearer ${token}` }
          });
          setIsAuthenticated(true);
        } catch {
          localStorage.removeItem('token');
          setIsAuthenticated(false);
        }
      }
    };

    checkAuth();
  }, []);

  const handleAuthSuccess = () => {
    setIsAuthenticated(true);
  };

  return (
    <Router>
      <Routes>
        <Route path="/auth" element={isAuthenticated ? <Navigate to="/" /> : <Auth onAuthSuccess={handleAuthSuccess} />} />
        <Route path="/domain" element={isAuthenticated ? <Domain /> : <Navigate to="/auth" />} />
        <Route path="/servers" element={isAuthenticated ? <Servers /> : <Navigate to="/auth" />} />
        <Route path="/" element={isAuthenticated ? <Home /> : <Navigate to="/auth" />} />
      </Routes>
    </Router>
  );
};

export default App;
