import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Domain.css';
import '@fortawesome/fontawesome-free/css/all.min.css';
import logo from '../assets/ctrl logo.webp'; // Update the path accordingly

const Domain = ({ }) => {
  const [data, setData] = useState([]);
  const [filteredData, setFilteredData] = useState([]);
  const [formVisible, setFormVisible] = useState(false);
  const [isEditMode, setIsEditMode] = useState(false);
  const [currentEditId, setCurrentEditId] = useState(null);
  const [form, setForm] = useState({
    domainName: '',
    clientName: '',
    number: '',
    registeredDate: '',
    expiryDate: '',
    reminders: '',
    status: '',
  });
  const [searchTerm, setSearchTerm] = useState('');

  const navigate = useNavigate();

  useEffect(() => {
    axios.get('http://localhost:5000/api/domains')
      .then(response => {
        setData(response.data);
        setFilteredData(response.data);
      })
      .catch(error => console.error('Error fetching data:', error));
  }, []);

  useEffect(() => {
    if (searchTerm === '') {
      setFilteredData(data);
    } else {
      setFilteredData(
        data.filter(item =>
          item.clientName.toLowerCase().includes(searchTerm.toLowerCase())
        )
      );
    }
  }, [searchTerm, data]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm(prevForm => ({
      ...prevForm,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (Object.values(form).every(field => field !== '')) {
      const request = isEditMode
        ? axios.put(`http://localhost:5000/api/domains/${currentEditId}`, form)
        : axios.post('http://localhost:5000/api/domains', form);

      request
        .then(response => {
          if (isEditMode) {
            setData(prevData => prevData.map(item => item._id === currentEditId ? response.data : item));
          } else {
            setData(prevData => [...prevData, response.data]);
          }
          resetForm();
        })
        .catch(error => console.error(isEditMode ? 'Error updating data:' : 'Error saving data:', error));
    }
  };

  const resetForm = () => {
    setForm({
      domainName: '',
      clientName: '',
      number: '',
      registeredDate: '',
      expiryDate: '',
      reminders: '',
      status: '',
    });
    setFormVisible(false);
    setIsEditMode(false);
    setCurrentEditId(null);
  };

  const handleDelete = (id) => {
    axios.delete(`http://localhost:5000/api/domains/${id}`)
      .then(() => setData(prevData => prevData.filter(item => item._id !== id)))
      .catch(error => console.error('Error deleting data:', error));
  };

  const handleEdit = (item) => {
    setForm(item);
    setCurrentEditId(item._id);
    setIsEditMode(true);
    setFormVisible(true);
  };

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchTerm) {
      setFilteredData(
        data.filter(item =>
          item.clientName.toLowerCase().includes(searchTerm.toLowerCase())
        )
        
      );
    } else {
      setFilteredData(data);
    }
  };

  return (
    <div className='dom'>
      <header className="domain-header">
        <img src={logo} alt="Logo" className="logo" />
      </header>
     
      <button className="back-button-domain" onClick={() => navigate('/home')}>
        <i className="fas fa-home"></i> 
      </button>
      <button className="form-toggle-button-domain" onClick={() => setFormVisible(true)}>
        <i className="fas fa-plus"></i> Add New Domain
      </button>
      
      <div className="search-bar-container">
        <form onSubmit={handleSearch}>
          <input
            type="text"
            placeholder="Search by client name..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="search-bar"
          />
          <button type="submit" className="search-button">
            <i className="fas fa-search"></i> Search
          </button>
        </form>
      </div>
      
      {formVisible && (
        <div className="form-container-domain">
          <div className="form-popup-domain">
            <h3>{isEditMode ? 'Edit Domain Details' : 'Add Domain Details'}</h3>
            <form onSubmit={handleSubmit}>
              <div className="form-row">
                <label>
                  Domain Name
                  <input
                    type="text"
                    name="domainName"
                    value={form.domainName}
                    onChange={handleChange}
                    required
                  />
                </label>
                <label>
                  Client Name
                  <input
                    type="text"
                    name="clientName"
                    value={form.clientName}
                    onChange={handleChange}
                    required
                  />
                </label>
                <label>
                  Contact Number
                  <input
                    type="text"
                    name="number"
                    value={form.number}
                    onChange={handleChange}
                    required
                  />
                </label>
                <label>
                  Registered Date
                  <input
                    type="date"
                    name="registeredDate"
                    value={form.registeredDate}
                    onChange={handleChange}
                    required
                  />
                </label>
                <label>
                  Expiry Date
                  <input
                    type="date"
                    name="expiryDate"
                    value={form.expiryDate}
                    onChange={handleChange}
                    required
                  />
                </label>
                <label>
                  Reminders
                  <select
                    name="reminders"
                    value={form.reminders}
                    onChange={handleChange}
                    required
                  >
                    <option value="">Select Reminders</option>
                    {[...Array(50).keys()].map(day => (
                      <option key={day + 1} value={day + 1}>
                        {day + 1} day{day > 0 ? 's' : ''}
                      </option>
                    ))}
                  </select>
                </label>
                <label>
                  Status
                  <select
                    name="status"
                    value={form.status}
                    onChange={handleChange}
                    required
                  >
                    <option value="">Select Status</option>
                    <option value="Active">Active</option>
                    <option value="Inactive">Inactive</option>
                  </select>
                </label>
              </div>
              <div className="form-buttons-domain">
                <button className="form-add-button-domain" type="submit">
                  {isEditMode ? 'Save Changes' : 'Add'}
                </button>
                <button className="form-cancel-button-domain" type="button" onClick={resetForm}>
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
      <div className="table-container">
        <table>
          <thead>
            <tr>
              <th>S.No</th>
              <th>Domain Name</th>
              <th>Client Name</th>
              <th>Number</th>
              <th>Registered Date</th>
              <th>Expiry Date</th>
              <th>Reminders</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {filteredData.map((item, index) => (
              <tr key={item._id} >
                <td>{index + 1}</td>
                <td>{item.domainName}</td>
                <td>{item.clientName}</td>
                <td>{item.number}</td>
                <td>{item.registeredDate}</td>
                <td>{item.expiryDate}</td>
                <td>{item.reminders}</td>
                <td>{item.status}</td>
                <td>
                  <button className="edit-button" onClick={(e) => { e.stopPropagation(); handleEdit(item); }}>
                    <i className="fas fa-edit"></i> Edit
                  </button>
                  <button className="delete-button" onClick={(e) => { e.stopPropagation(); handleDelete(item._id); }}>
                    <i className="fas fa-trash-alt"></i> Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Domain;
