import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Domain from './components/Domain';
import Servers from './components/Servers';
import Home from './components/Home';
import Auth from './components/Auth';
import axios from 'axios';

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loading, setLoading] = useState(true); // Handle async auth check

  useEffect(() => {
    const checkAuth = async () => {
      const token = localStorage.getItem('token');
      if (token) {
        try {
          await axios.get('/api/auth/me', {
            headers: { Authorization: `Bearer ${token}` }
          });
          setIsAuthenticated(true);
        } catch {
          localStorage.removeItem('token');
          setIsAuthenticated(false);
        }
      }
      setLoading(false); // Set loading to false once the auth check is complete
    };

    checkAuth();
  }, []);

  const handleAuthSuccess = () => {
    setIsAuthenticated(true);
  };

  if (loading) {
    return <div>Loading...</div>; // Show loading state while checking authentication
  }

  return (
    <Router>
      <Routes>
        <Route path="/auth" element={isAuthenticated ? <Navigate to="/home" /> : <Auth onAuthSuccess={handleAuthSuccess} />} />
        <Route path="/domain" element={isAuthenticated ? <Domain /> : <Navigate to="/auth" />} />
        <Route path="/servers" element={isAuthenticated ? <Servers /> : <Navigate to="/auth" />} />
        <Route path="/home" element={isAuthenticated ? <Home /> : <Navigate to="/auth" />} />
        <Route path="*" element={<Navigate to="/auth" />} /> {/* Redirect undefined paths to /auth */}
      </Routes>
    </Router>
  );
};

export default App;
